mapper.update:
UPDATE transaction_operation
SET status = #{status}
WHERE id=#{id}

mapper.select:
SELECT id,no,transaction_id,topic,name,operation,params,status,create_time FROM transaction_operation WHERE status = 0 AND transaction_id IN
#{obj.id}
ORDER BY transaction_id,no

SELECT id,no,transaction_id,topic,name,operation,params,status,create_time FROM transaction_operation WHERE transaction_id IN
#{obj.id}
ORDER BY transaction_id,no
